function JALB_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('JALB', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: JALB_before_delete
	if(function_exists('JALB_before_delete')) {
		$args = [];
		if(!JALB_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `JALB` WHERE `id`='{$selected_id}'", $eo);

	// hook: JALB_after_delete
	if(function_exists('JALB_after_delete')) {
		$args = [];
		JALB_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='JALB' AND `pkValue`='{$selected_id}'", $eo);
}

