			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Tahun_daftar').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Tahun_daftar_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#negeri').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#negeri_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#daerah').replaceWith('<div class=\"form-control-static\" id=\"daerah\">' + (jQuery('#daerah').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#nama_pemilik').replaceWith('<div class=\"form-control-static\" id=\"nama_pemilik\">' + (jQuery('#nama_pemilik').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#nama_syarikat').replaceWith('<div class=\"form-control-static\" id=\"nama_syarikat\">' + (jQuery('#nama_syarikat').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Alamat').replaceWith('<div class=\"form-control-static\" id=\"Alamat\">' + (jQuery('#Alamat').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#no_ssm').replaceWith('<div class=\"form-control-static\" id=\"no_ssm\">' + (jQuery('#no_ssm').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#no_cidb').replaceWith('<div class=\"form-control-static\" id=\"no_cidb\">' + (jQuery('#no_cidb').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#no_evendor').replaceWith('<div class=\"form-control-static\" id=\"no_evendor\">' + (jQuery('#no_evendor').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email').replaceWith('<div class=\"form-control-static\" id=\"email\">' + (jQuery('#email').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(Tahun_daftar)%%>', $combo_Tahun_daftar->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Tahun_daftar)%%>', $combo_Tahun_daftar->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Tahun_daftar)%%>', urlencode($combo_Tahun_daftar->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(negeri)%%>', $combo_negeri->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(negeri)%%>', $combo_negeri->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(negeri)%%>', urlencode($combo_negeri->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('Tahun_daftar' => array('tahun', 'Tahun Daftar'), 'negeri' => array('negeri', 'Negeri'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Tahun_daftar)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(negeri)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(daerah)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nama_pemilik)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(nama_syarikat)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Alamat)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(no_ssm)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(no_cidb)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(no_evendor)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(email)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Tahun_daftar)%%>', safe_html($urow['Tahun_daftar']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Tahun_daftar)%%>', html_attr($row['Tahun_daftar']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Tahun_daftar)%%>', urlencode($urow['Tahun_daftar']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(negeri)%%>', safe_html($urow['negeri']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(negeri)%%>', html_attr($row['negeri']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(negeri)%%>', urlencode($urow['negeri']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(daerah)%%>', safe_html($urow['daerah']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(daerah)%%>', html_attr($row['daerah']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(daerah)%%>', urlencode($urow['daerah']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nama_pemilik)%%>', safe_html($urow['nama_pemilik']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nama_pemilik)%%>', html_attr($row['nama_pemilik']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama_pemilik)%%>', urlencode($urow['nama_pemilik']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(nama_syarikat)%%>', safe_html($urow['nama_syarikat']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(nama_syarikat)%%>', html_attr($row['nama_syarikat']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama_syarikat)%%>', urlencode($urow['nama_syarikat']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(Alamat)%%>', safe_html($urow['Alamat']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(Alamat)%%>', safe_html($urow['Alamat'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(Alamat)%%>', urlencode($urow['Alamat']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(no_ssm)%%>', safe_html($urow['no_ssm']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(no_ssm)%%>', html_attr($row['no_ssm']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_ssm)%%>', urlencode($urow['no_ssm']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(no_cidb)%%>', safe_html($urow['no_cidb']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(no_cidb)%%>', html_attr($row['no_cidb']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_cidb)%%>', urlencode($urow['no_cidb']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(no_evendor)%%>', safe_html($urow['no_evendor']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(no_evendor)%%>', html_attr($row['no_evendor']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_evendor)%%>', urlencode($urow['no_evendor']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(email)%%>', safe_html($urow['email']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(email)%%>', html_attr($row['email']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email)%%>', urlencode($urow['email']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Tahun_daftar)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Tahun_daftar)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(negeri)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(negeri)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(daerah)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(daerah)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nama_pemilik)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama_pemilik)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(nama_syarikat)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(nama_syarikat)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Alamat)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Alamat)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(no_ssm)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_ssm)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(no_cidb)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_cidb)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(no_evendor)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(no_evendor)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(email)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(email)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Syarikat_baru');
	if($selected_id) {
		$jdata = get_joined_record('Syarikat_baru', $selected_id);
		if($jdata === false) $jdata = get_defaults('Syarikat_baru');
		$rdata = $row;
	}
	$templateCode .= loadView('Syarikat_baru-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: Syarikat_baru_dv
	if(function_exists('Syarikat_baru_dv')) {
		$args=[];
		Syarikat_baru_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}