function Syarikat_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Syarikat');
	if(!$arrPerm['insert']) return false;

	$data = [
		'negeri' => Request::lookup('negeri', ''),
		'nama_syarikat' => Request::val('nama_syarikat', ''),
		'Alamat' => br2nl(Request::val('Alamat', '')),
		'no_ssm' => Request::val('no_ssm', ''),
		'no_cidb' => Request::val('no_cidb', ''),
		'lantik_kplb' => Request::val('lantik_kplb', ''),
		'Tahun_lantik' => Request::lookup('Tahun_lantik', ''),
		'tarikh_dokumen' => Request::dateComponents('tarikh_dokumen', ''),
		'tajuk_kerja' => br2nl(Request::val('tajuk_kerja', '')),
		'kaedah_perolehan' => Request::val('kaedah_perolehan', ''),
		'kos_projek' => Request::val('kos_projek', ''),
		'blacklist' => Request::lookup('blacklist', ''),
		'catatan' => br2nl(Request::val('catatan', '')),
	];


	// hook: Syarikat_before_insert
	if(function_exists('Syarikat_before_insert')) {
		$args = [];
		if(!Syarikat_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Syarikat', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('Syarikat', $recID, calculated_fields()['Syarikat']);

	// hook: Syarikat_after_insert
	if(function_exists('Syarikat_after_insert')) {
		$res = sql("SELECT * FROM `Syarikat` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!Syarikat_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Syarikat', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Syarikat_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function Syarikat_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function Syarikat_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('Syarikat', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: Syarikat_before_delete
	if(function_exists('Syarikat_before_delete')) {
		$args = [];
		if(!Syarikat_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `Syarikat` WHERE `id`='{$selected_id}'", $eo);

	// hook: Syarikat_after_delete
	if(function_exists('Syarikat_after_delete')) {
		$args = [];
		Syarikat_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='Syarikat' AND `pkValue`='{$selected_id}'", $eo);
}

function Syarikat_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Syarikat', $selected_id, 'edit')) return false;

	$data = [
		'negeri' => Request::lookup('negeri', ''),
		'nama_syarikat' => Request::val('nama_syarikat', ''),
		'Alamat' => br2nl(Request::val('Alamat', '')),
		'no_ssm' => Request::val('no_ssm', ''),
		'no_cidb' => Request::val('no_cidb', ''),
		'lantik_kplb' => Request::val('lantik_kplb', ''),
		'Tahun_lantik' => Request::lookup('Tahun_lantik', ''),
		'tarikh_dokumen' => Request::dateComponents('tarikh_dokumen', ''),
		'tajuk_kerja' => br2nl(Request::val('tajuk_kerja', '')),
		'kaedah_perolehan' => Request::val('kaedah_perolehan', ''),
		'kos_projek' => Request::val('kos_projek', ''),
		'blacklist' => Request::lookup('blacklist', ''),
		'catatan' => br2nl(Request::val('catatan', '')),
	];

	// get existing values
	$old_data = getRecord('Syarikat', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Syarikat_before_update
	if(function_exists('Syarikat_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Syarikat_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Syarikat', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Syarikat_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Syarikat', $data['selectedID'], calculated_fields()['Syarikat']);

	// hook: Syarikat_after_update
	if(function_exists('Syarikat_after_update')) {
		$res = sql("SELECT * FROM `Syarikat` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!Syarikat_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='Syarikat' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

