function ljk_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('ljk');
	if(!$arrPerm['insert']) return false;

	$data = [
		'negeri' => Request::lookup('negeri', ''),
		'daerah' => Request::lookup('daerah', ''),
		'dun' => Request::lookup('dun', ''),
		'parlimen' => Request::lookup('parlimen', ''),
		'kampung' => Request::val('kampung', ''),
		'no_tiang' => Request::val('no_tiang', ''),
		'jenis_lampu' => Request::lookup('jenis_lampu', ''),
		'status_pemasangan' => Request::val('status_pemasangan', ''),
		'audit' => Request::val('audit', ''),
		'catatan' => br2nl(Request::val('catatan', '')),
		'gambar' => Request::fileUpload('gambar', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => true,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('ljk', 'gambar', $_REQUEST['SelectedID']);
			},
		]),
	];


	// hook: ljk_before_insert
	if(function_exists('ljk_before_insert')) {
		$args = [];
		if(!ljk_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('ljk', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('ljk', $recID, calculated_fields()['ljk']);

	// hook: ljk_after_insert
	if(function_exists('ljk_after_insert')) {
		$res = sql("SELECT * FROM `ljk` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!ljk_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('ljk', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) ljk_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function ljk_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function ljk_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('ljk', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: ljk_before_delete
	if(function_exists('ljk_before_delete')) {
		$args = [];
		if(!ljk_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `ljk` WHERE `id`='{$selected_id}'", $eo);

	// hook: ljk_after_delete
	if(function_exists('ljk_after_delete')) {
		$args = [];
		ljk_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='ljk' AND `pkValue`='{$selected_id}'", $eo);
}

function ljk_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('ljk', $selected_id, 'edit')) return false;

	$data = [
		'negeri' => Request::lookup('negeri', ''),
		'daerah' => Request::lookup('daerah', ''),
		'dun' => Request::lookup('dun', ''),
		'parlimen' => Request::lookup('parlimen', ''),
		'kampung' => Request::val('kampung', ''),
		'no_tiang' => Request::val('no_tiang', ''),
		'jenis_lampu' => Request::lookup('jenis_lampu', ''),
		'status_pemasangan' => Request::val('status_pemasangan', ''),
		'audit' => Request::val('audit', ''),
		'catatan' => br2nl(Request::val('catatan', '')),
		'gambar' => Request::fileUpload('gambar', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => true,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('ljk', 'gambar', $selected_id);
			},
		]),
	];

	// get existing values
	$old_data = getRecord('ljk', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: ljk_before_update
	if(function_exists('ljk_before_update')) {
		$args = ['old_data' => $old_data];
		if(!ljk_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'ljk', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="ljk_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('ljk', $data['selectedID'], calculated_fields()['ljk']);

	// hook: ljk_after_update
	if(function_exists('ljk_after_update')) {
		$res = sql("SELECT * FROM `ljk` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!ljk_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='ljk' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

