	$templateCode = str_replace('<%%URLCOMBOTEXT(parlimen)%%>', urlencode($combo_parlimen->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(jenis_lampu)%%>', $combo_jenis_lampu->HTML, $templateCode);
