		if( $dvprint) $templateCode = str_replace('<%%VALUE(daerah)%%>', safe_html($urow['daerah']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(daerah)%%>', html_attr($row['daerah']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(daerah)%%>', urlencode($urow['daerah']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(dun)%%>', safe_html($urow['dun']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(dun)%%>', html_attr($row['dun']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(dun)%%>', urlencode($urow['dun']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(parlimen)%%>', safe_html($urow['parlimen']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(parlimen)%%>', html_attr($row['parlimen']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(parlimen)%%>', urlencode($urow['parlimen']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(kampung)%%>', safe_html($urow['kampung']), $templateCode);
