		$templateCode = str_replace('<%%URLVALUE(no_tiang)%%>', urlencode($urow['no_tiang']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(jenis_lampu)%%>', safe_html($urow['jenis_lampu']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(jenis_lampu)%%>', html_attr($row['jenis_lampu']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(jenis_lampu)%%>', urlencode($urow['jenis_lampu']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(status_pemasangan)%%>', safe_html($urow['status_pemasangan']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(status_pemasangan)%%>', html_attr($row['status_pemasangan']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(status_pemasangan)%%>', urlencode($urow['status_pemasangan']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(audit)%%>', safe_html($urow['audit']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(audit)%%>', html_attr($row['audit']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(audit)%%>', urlencode($urow['audit']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(catatan)%%>', safe_html($urow['catatan']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(catatan)%%>', safe_html($urow['catatan'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(catatan)%%>', urlencode($urow['catatan']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(gambar)%%>', safe_html($urow['gambar']), $templateCode);
