	update_calc_fields('status_kelulusan_pams', $recID, calculated_fields()['status_kelulusan_pams']);

	// hook: status_kelulusan_pams_after_insert
	if(function_exists('status_kelulusan_pams_after_insert')) {
		$res = sql("SELECT * FROM `status_kelulusan_pams` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
