		$args=[];
		if(!status_kelulusan_pams_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('status_kelulusan_pams', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) status_kelulusan_pams_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

