function status_kelulusan_pams_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('status_kelulusan_pams', $selected_id, 'edit')) return false;

	$data = [
		'pemohon' => Request::lookup('pemohon', ''),
		'kelulusan' => Request::val('kelulusan', ''),
	];

	// get existing values
	$old_data = getRecord('status_kelulusan_pams', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: status_kelulusan_pams_before_update
	if(function_exists('status_kelulusan_pams_before_update')) {
		$args = ['old_data' => $old_data];
		if(!status_kelulusan_pams_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'status_kelulusan_pams', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="status_kelulusan_pams_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('status_kelulusan_pams', $data['selectedID'], calculated_fields()['status_kelulusan_pams']);

	// hook: status_kelulusan_pams_after_update
	if(function_exists('status_kelulusan_pams_after_update')) {
		$res = sql("SELECT * FROM `status_kelulusan_pams` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!status_kelulusan_pams_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='status_kelulusan_pams' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

